mapper.select:
SELECT *,(SELECT `name` FROM sys_account WHERE tb2.accountId = id ) AS optName FROM ( select * from ( select * from (SELECT a.id AS shareId,b.id AS customerId,a.`state`,b.name,b.phone, a.`order`,b.short_name AS shortName,b.`status`,a.is_own as isOwn, b.name_simple_py AS nameSimplePy,b.name_py AS namePy, sales_success_rate AS salesSuccessRate, a.`is_from` AS isFrom, a.`account_id` AS accountId, (SELECT ac.`name` from sys_account ac WHERE ac.id=b.`create_id`) AS createName, a.`allow_account_id` AS allowAccountId, (SELECT e.name FROM sys_customer_share AS f LEFT JOIN sys_account e ON f.account_id = e.id WHERE f.state = 0 AND f.is_own = 1 AND f.customer_id=a.`customer_id`) AS allowAccountName,
(SELECT `name` FROM sys_dict WHERE id = b.`status`) AS statusName,
(SELECT MAX(communication_time)FROM sys_customer_record WHERE customer_id =a.`customer_id`) AS finalTime,b.province,
(SELECT `name` FROM sys_prov_city_area_street WHERE `code` = b.province) AS provinceName, b.`type`,
(SELECT GROUP_CONCAT(dict.`name`) FROM sys_dict AS dict WHERE LOCATE(CONCAT(",",CONCAT(dict.id,",")),CONCAT(",",CONCAT(b.`type`,",")))) AS typeName, b.create_time AS createTime, b.recommender,b.buy_service as buyService,b.cus_source as cusSource,b.cus_source_type as cusSourceType,b.synopsis, b.business,b.pay_nature as payNature,b.follow_state as followState,b.follow_state_details as followStateDetails FROM sys_customer_share AS a LEFT JOIN sys_customer AS b ON a.customer_id = b.id LEFT JOIN sys_account AS g ON a.`allow_account_id` = g.id WHERE a.`state`=0 AND b.`state` = 0 ) as tb where tb.state=0
and (tb.name like CONCAT(CONCAT('%',#{content}),'%') or tb.shortName like CONCAT(CONCAT('%', #{content}),'%') or tb.nameSimplePy like CONCAT(CONCAT('%', #{content}),'%') or tb.namePy like CONCAT(CONCAT('%', #{content}),'%') or tb.phone like CONCAT(CONCAT('%', #{content}),'%')CustomerListSearch )
and TO_DAYS(NOW()) - TO_DAYS(tb.finalTime) >= #{traceType}
and tb.finalTime is null
and tb.cusSource = #{cusSource}
and tb.buyService = #{buyService}
and tb.followState = #{followState}
and tb.status = #{status}
and tb.isFrom=#{isFrom }
and locate(concat(",",concat(#{customerType},",")),concat(",",concat(tb.type,",")))
and locate(concat(",",concat(#{salesSuccessRate},",")),concat(",",concat(tb.salesSuccessRate,",")))
and locate(concat(",",concat(tb.allowAccountId,",")),concat(",",concat(#{principalStr},",")))
order by tb.`isOwn` desc ) as tb1 ) as tb2 group by tb2.`customerId`
order by tb2.`order` desc
order by tb2.`${orderField}` ${orderType}

select * from
sys_customer_share
where id = #{value}

mapper.update:
update sys_customer_share set `order`=`order`-1 where
state = 0 and
allow_account_id=#{0} and `order` >#{1}

mapper.select:
	select id, customer_id, channel_partner, trust_person, decision_person, manage_person, handle_person,
    professional_person, create_id, create_time from sys_customer_relations
    where customer_id = #{customerId}
